op_cost_minus_lab = sum(params.optiParams.km_cost*(...
    [output.optiVars.xit_extra output.optiVars.xit_adj]*output.process.dist_mat(params.preParams.dump_ind,[output.process.skip_nums_extra_scens output.process.indices_skips])'...
    + (output.process.dist_mat([output.process.skip_nums_extra_scens output.process.indices_skips],params.preParams.dump_ind)'*[output.optiVars.xit_extra output.optiVars.xit_adj]')'...
    + [output.optiVars.fit_extra output.optiVars.fit]*((-1*output.process.dist_mat(params.preParams.dump_ind,[output.process.skip_nums_extra_scens output.process.indices_skips]) + output.process.dist_mat(params.preParams.depot_ind,[output.process.skip_nums_extra_scens output.process.indices_skips])))'...
    + output.optiVars.numV_D*output.process.dist_mat(params.preParams.dump_ind,params.preParams.depot_ind)))

lab_cost = sum(params.optiParams.period_op_cost.*output.optiVars.numV_crews)
tot_cost = op_cost_minus_lab + lab_cost